import React, { useState, useEffect } from "react";
import "./App.css";
import Forecast from "./Forecast";
import sunnyImage from "../src/assets/clearskies.jpg";
import cloudyImage from "../src/assets/clouds.jpg";
import rainyImage from "../src/assets/rain.jpg";
import snowyImage from "../src/assets/snow.png";

function App() {
  const [city, setCity] = useState("New York");
  const [temperature, setTemperature] = useState(75);
  const [description, setDescription] = useState("🌤️Clear Sky");
  const [humidity, setHumidity] = useState(60);
  const [windSpeed, setWindSpeed] = useState(5);
  const [time, setTime] = useState("");
  const [forecastData, setForecastData] = useState([]);

  useEffect(() => {
    const fetchWeatherData = async () => {
      setTemperature(Math.floor(Math.random() * 41));
      setDescription("🌤️Clear Sky");
      setHumidity(Math.floor(Math.random() * 101));
      setWindSpeed(Math.floor(Math.random() * 21));

      const currentTime = new Date().toLocaleString("en-US", {
        hour: "numeric",
        minute: "numeric",
        hour12: true,
      });
      setTime(currentTime);

      const forecastData = [
        { day: "Monday", temperature: 23, description: "🌤️ Clear", humidity: 65, windSpeed: 10 },
        { day: "Tuesday", temperature: 25, description: "⛅ Partly cloudy", humidity: 60, windSpeed: 12 },
        { day: "Wednesday", temperature: 22, description: "🌦️ Light rain showers", humidity: 70, windSpeed: 8 },
        { day: "Thursday", temperature: 24, description: "🌤️ Clear", humidity: 62, windSpeed: 11 },
        { day: "Friday", temperature: 26, description: "🌞 Sunny", humidity: 58, windSpeed: 14 }
      ];
      setForecastData(forecastData);
    };

    fetchWeatherData();
  }, []);

  const getWeatherImage = (description) => {
    switch (description) {
      case "🌤️Clear Sky":
        return sunnyImage;
      case "⛅Partly cloudy":
        return cloudyImage;
      case "🌦️Light rain showers":
        return rainyImage;
      case "🌞Sunny":
        return sunnyImage;
      default:
        return ""; 
    }
  };

  const handleSearch = (event) => {
    event.preventDefault();
    const cityInput = event.target.elements.city.value;
    setCity(cityInput);
    setTemperature(Math.floor(Math.random() * 41));
    setDescription("🌤️Clear Sky");
    setHumidity(Math.floor(Math.random() * 101));
    setWindSpeed(Math.floor(Math.random() * 21));
    const currentTime = new Date().toLocaleString("en-US", {
      hour: "numeric",
      minute: "numeric",
      hour12: true,
    });
    setTime(currentTime);
  };

  return (
    <div className="weather-app">
      <header className="app-header">
        <form className="search-form" onSubmit={handleSearch}>
          <input
            type="search"
            name="city"
            placeholder="Enter a city..."
            required
            className="search-form-input"
          />
          <input type="submit" value="Search" className="search-form-button" />
        </form>
        <div className="weather-info">
          <h1>{city}</h1>
          <img src={getWeatherImage(description)} alt={description} className="weather-image" />
          <p>Temperature: {temperature}°C</p>
          <p>Humidity: {humidity}%</p>
          <p>Wind Speed: {windSpeed} km/h</p>
          <p>Time: {time}</p>
        </div>
      </header>
      <main>
        <Forecast forecastData={forecastData} />
      </main>
      <Footer />
    </div>
  );
}

function Footer() {
  return (
    <footer>
      This project was coded by <a href="https://github.com/Stephaniechatter" target="_blank" rel="noopener noreferrer">Stephanie White</a> and is open-sourced on{" "}
      <a href="https://github.com/your-github-repo" target="_blank" rel="noopener noreferrer">
        GitHub
      </a>{" "}
      and hosted on{" "}
      <a href="https://www.netlify.com/" target="_blank" rel="noopener noreferrer">
        Netlify
      </a>
      .
    </footer>
  );
}

export default App;
