import React from "react";
import sunnyImage from "../src/assets/clearskies.jpg";
import cloudyImage from "../src/assets/clouds.jpg";
import rainyImage from "../src/assets/rain.jpg";
import snowyImage from "../src/assets/snow.png";

function Forecast({ forecastData }) {
  // Function to abbreviate days of the week
  const abbreviateDay = (day) => {
    return day.slice(0, 3); // Return the first three letters of the day
  };

  // Function to map weather description to corresponding image
  const getWeatherImage = (description) => {
    switch (description) {
      case "üå§Ô∏è Clear":
        return sunnyImage;
      case "‚õÖ Partly cloudy":
        return partlySnowyImage;
      case "üå¶Ô∏è Light rain showers":
        return rainyImage;
      case "üåû Sunny":
        return sunnyImage;
      default:
        return ""; // Default image if no match
    }
  };

  // Slice forecastData to show only 4 days
  const fourDayForecast = forecastData.slice(0, 4);

  return (
    <div className="forecast-container">
      <h2>4-Day Forecast</h2>
      <div className="forecast-list">
        {fourDayForecast.map((dayForecast, index) => (
          <div className="forecast-item" key={index}>
            <div className="day">{abbreviateDay(dayForecast.day)}</div>
            <img
              src={getWeatherImage(dayForecast.description)}
              alt={dayForecast.description}
            />
          </div>
        ))}
      </div>
    </div>
  );
}

export default Forecast;
