import React from "react";
import "./Forecast.css";

function Forecast({ forecastData }) {
  // Function to abbreviate days of the week
  const abbreviateDay = (day) => {
    return day.slice(0, 3); // Return the first three letters of the day
  };

  return (
    <div className="forecast-container">
      <h2>4-Day Forecast</h2>
      <div className="forecast-list">
        {forecastData.map((dayForecast, index) => (
          <div className="forecast-item" key={index}>
            <div className="day">{abbreviateDay(dayForecast.day)}</div>
            <div className="emoji">{dayForecast.icon}</div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Forecast;
