import React from "react";

function Forecast({ forecastData }) {
  // Function to abbreviate days of the week
  const abbreviateDay = (day) => {
    return day.slice(0, 3); // Return the first three letters of the day
  };

  // Function to map weather description to icons
  const getWeatherIcon = (description) => {
    switch (description) {
      case "üå§Ô∏è Clear":
        return "‚òÄÔ∏è";
      case "‚õÖ Partly cloudy":
        return "‚õÖ";
      case "üå¶Ô∏è Light rain showers":
        return "üå¶Ô∏è";
      case "üåû Sunny":
        return "üåû";
      default:
        return "üåç"; // Default icon if no match
    }
  };

  // Slice forecastData to show only 4 days
  const fourDayForecast = forecastData.slice(0, 4);

  return (
    <div className="forecast-container">
      <h2>4-Day Forecast</h2>
      <div className="forecast-list">
        {fourDayForecast.map((dayForecast, index) => (
          <div className="forecast-item" key={index}>
            <div className="day">{abbreviateDay(dayForecast.day)}</div>
            <div>{getWeatherIcon(dayForecast.description)}</div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Forecast;
