import React, { useState, useEffect } from "react";
import "./App.css";

function App() {
  const [city, setCity] = useState("New York");
  const [temperature, setTemperature] = useState(75);
  const [description, setDescription] = useState("‚òÄÔ∏è Sunny");
  const [humidity, setHumidity] = useState(60);
  const [windSpeed, setWindSpeed] = useState(5);
  const [time, setTime] = useState("");
  const [forecast, setForecast] = useState([
    { day: "Mon", emoji: "‚òÄÔ∏è" },
    { day: "Tue", emoji: "üå§Ô∏è" },
    { day: "Wed", emoji: "‚õÖ" },
    { day: "Thu", emoji: "üå¶Ô∏è" },
  ]);

  useEffect(() => {
    const fetchWeatherData = async () => {
      // Simulated weather data
      setTemperature(Math.floor(Math.random() * 41));
      setDescription("‚òÄÔ∏è Sunny");
      setHumidity(Math.floor(Math.random() * 101));
      setWindSpeed(Math.floor(Math.random() * 21));

      // Simulated time
      const currentTime = new Date().toLocaleString("en-US", {
        hour: "numeric",
        minute: "numeric",
        hour12: true,
      });
      setTime(currentTime);
    };

    fetchWeatherData();
  }, []); // Empty dependency array ensures this effect runs only once

  // Function to handle city search
  const handleSearch = (event) => {
    event.preventDefault();
    const cityInput = event.target.elements.city.value;
    setCity(cityInput);
    // Simulate fetching data for the new city (temperature, weather description, etc.)
    setTemperature(Math.floor(Math.random() * 41));
    setDescription("‚òÄÔ∏è Sunny");
    setHumidity(Math.floor(Math.random() * 101));
    setWindSpeed(Math.floor(Math.random() * 21));
    // Simulate updating time
    const currentTime = new Date().toLocaleString("en-US", {
      hour: "numeric",
      minute: "numeric",
      hour12: true,
    });
    setTime(currentTime);
  };

  return (
    <div className="weather-app">
      <header>
        <form className="search-form" id="search-form" onSubmit={handleSearch}>
          <input
            type="search"
            name="city"
            placeholder="Enter a city..."
            required
            id="search-form-input"
            className="search-form-input"
          />
          <input type="submit" value="Search" className="search-form-button" />
        </form>
      </header>
      <main>
        {/* Displayed city information */}
        <div className="weather-app-data">
          <h1 className="default-city-name">{city}</h1>
          <p className="weather-app-image">{description}</p>
          <p className="weather-app-details">
            Temperature: <strong>{temperature}¬∞C</strong>, Humidity:{" "}
            <strong>{humidity}%</strong>, Wind: <strong>{windSpeed} km/h</strong>,
            Time: <strong>{time}</strong>
          </p>
        </div>

        {/* Forecast */}
        <div className="forecast-container">
          <h2>4-Day Forecast</h2>
          <div className="forecast-list">
            {forecast.map((item, index) => (
              <div key={index} className="forecast-item">
                <p>{item.emoji}</p>
                <p>{item.day}</p>
              </div>
            ))}
          </div>
        </div>
      </main>
      <Footer />
    </div>
  );
}

function Footer() {
  return (
    <footer>
      This project was coded by Stephanie White
      <a href="https://example.com" target="_blank" rel="noreferrer">github.com/setphaniechatter
      and is <a href="github.com/stephaniechatter/whimsicalweatherapp"target="_blank"rel="noreferrer">Github
      open sourced</a>
        and hosted on <a href="https://example.com" target="_blank" rel="noreferrer">Netflify</a>

      </a>
      .
    </footer>
  );
}

export default App;
