// Forecast component
import React from "react";

function Forecast({ forecastData }) {
  const getWeatherIcon = (description) => {
    switch (description) {
      case "🌤️ Clear":
        return "☀️";
      case "⛅ Partly cloudy":
        return "⛅";
      case "🌦️ Light rain showers":
        return "🌧️";
      case "🌞 Sunny":
        return "🌞";
      default:
        return "🌼";
    }
  };

  // Ensure we have at least 4 days of forecast data
  const forecastItems = forecastData.length >= 4 ? forecastData.slice(0, 4) : forecastData;

  return (
    <div className="forecast-container">
      <h2>4-Day Forecast</h2>
      <div className="forecast-list">
        {forecastItems.map((day, index) => (
          <div className="forecast-item" key={index}>
            <div className="day">{day.day}</div>
            <div className="weather-icon">{getWeatherIcon(day.description)}</div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Forecast;
