import React, { useEffect, useState } from "react";
import axios from "axios";
import clearSkies from "..src/assets/clearSkies.jpg"
import cloudy from "./assets/cloudy.jpg";
import rain from "./assets/rain.jpg";
import thunderstorm from "./assets/thunderstorm.jpg";
import drizzle from "./assets/drizzle.jpg";
import snow from "./assets/snow.jpg";
import mist from "./assets/mist.jpg";

const weatherIcons = {
  Clear: clearSkies,
  Clouds: cloudy,
  Rain: rain,
  Thunderstorm: thunderstorm,
  Drizzle: drizzle,
  Snow: snow,
  Mist: mist,
};

export default function Weather({ city }) {
  const [temperature, setTemperature] = useState("");
  const [description, setDescription] = useState("");
  const [humidity, setHumidity] = useState("");
  const [windSpeed, setWindSpeed] = useState("");

  useEffect(() => {
    const apiKey = "ab8e7ef210556986d1c9a75d6007b825";
    const apiUrl = `https://api.openweathermap.org/data/2.5/weather?q=${city}&appid=${apiKey}&units=metric`;

    axios
      .get(apiUrl)
      .then((response) => {
        setTemperature(response.data.main.temp);
        setDescription(response.data.weather[0].main);
        setHumidity(response.data.main.humidity);
        setWindSpeed(response.data.wind.speed);
      })
      .catch((error) => {
        console.error("Error fetching weather data:", error);
      });
  }, [city]);

  const getWeatherIcon = (weatherDescription) => {
    return <img src={weatherIcons[weatherDescription]} alt={weatherDescription} className="weather-app-icon" /> || "❓";
  };

  return (
    <div className="weather-app-data">
      <h1 className="weather-app-city">{city}</h1>
      <p className="weather-app-details">
        {getWeatherIcon(description)}
        <br />
        Temperature: <strong>{temperature}°C</strong>, Humidity:{" "}
        <strong>{humidity}%</strong>, Wind: <strong>{windSpeed} km/h</strong>
      </p>
    </div>
  );
}
